<?phpnamespace Share\Model;use Think\Model;class CategoryModel extends Model {    protected $tableName = 'share_category';    protected $pk        = 'id';        /**     * 类别列表     * @return array     * $type:1后台，2同城附近，3同城发现空间号     */    function public_list($type = 1) {        if($type == 1 || $type == 3){            $list = $this->select();        }else{            $list = $this->where('is_show=0')->select();        }        if ($list){            foreach ($list as $k=>&$v){                $v['logo'] = $v['logo'] ? SITE_PROTOCOL.SITE_URL.$v['logo'] : '';                $v['press_logo'] = $v['press_logo'] ? SITE_PROTOCOL.SITE_URL.$v['press_logo'] : '';            }        }        return $list ? $list : array();    }        /**     * 类别列表     */    function lists($type = 1) {        $data = self::public_list($type);        return showData($data);    }}